FROM test.wowemu0 t1
LEFT JOIN mangos.item_template t2 ON t2.entry = t1.entry
WHERE (t1.spellid_1 NOT IN (t2.spellid_1, t2.spellid_2, t2.spellid_3)) && (t2.patch = 0)

